import React, { useState } from "react";
import logo from './logo.svg';
import './App.css';
import { QRCode } from 'react-qrcode-logo';
import { TwitterPicker } from 'react-color';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';


const useStyles = makeStyles({
  root: {
    color: 'white'
  },
});

function App() {
  const classes = useStyles();
  const [urlText, setUrlText] = useState("https://github.com/gcoro/react-qrcode-logo");
  const [backgroundColor, setBackColor] = useState('#FFFFFF');
  const [fgColor, setFgColor] = useState('#000000');
  const [qrStyle, setQrStyle] = useState('squares');



  function handleClick() {
    return setUrlText("https://www.npmjs.com/package/react-qrcode-logo");
  };

  function handleChangeBg(color){
    return setBackColor(color);
  };

  function handleChangeFg(color){
    return setFgColor(color);
  };



  function handleUrl(event){
    return setUrlText(event);
  };

  function toggleStyle(){
    if(qrStyle == 'dots'){
      return setQrStyle('squares');
    }
    return setQrStyle('dots');
  }


  return (
    <div className="App">
      <header className="App-header">
        <button onClick={handleClick}>change url</button>
        <QRCode value={urlText} bgColor={backgroundColor}  fgColor={fgColor} qrStyle={qrStyle} size={225} />
        <TextField id="outlined-basic" label="URL" variant="outlined"
          placeholder="https://github.com/gcoro/react-qrcode-logo"
          onChange={event => handleUrl(event.target.value)}
          style={{

          }}
          InputProps={{
          style: {
              color: "white"
          }
          }}
        />


        <TwitterPicker color={ backgroundColor } onChangeComplete={({hex}) => { handleChangeBg(hex);}} triangle="hide"/>
        <TwitterPicker color={ fgColor } onChangeComplete={({hex}) => { handleChangeFg(hex);}} triangle="hide"/>

        <button onClick={toggleStyle}>Change Style</button>

      </header>
    </div>
  );
}

export default App;
