import React, { useState } from "react";
import logo from './logo.svg';
import './App.css';
import { QRCode } from 'react-qrcode-logo';
import { TwitterPicker } from 'react-color';





function App() {
  const [urlText, setUrlText] = useState("https://github.com/gcoro/react-qrcode-logo");
  const [backgroundColor, setBackColor] = useState('#FFFFFF');

  function handleClick() {
    return setUrlText("https://www.npmjs.com/package/react-qrcode-logo");
  };

  function handleChangeComplete(color){
    return setBackColor(color);
  };



  function handleUrl(event){
    return setUrlText(event);
  };


  return (
    <div className="App">
      <header className="App-header">
        <button onClick={handleClick}>change url</button>
        <QRCode value={urlText} bgColor={backgroundColor} />
        <label for="url">Enter an https:// URL:</label>

      <input type="url" name="url" id="url"
       placeholder="https://github.com/gcoro/react-qrcode-logo"
       pattern="https://.*" size="60"
       required onChange={event => handleUrl(event.target.value)}></input>

        <TwitterPicker onChangeComplete={({hex}) => {
        handleChangeComplete(hex);
      }}  triangle="hide"/>;
       <p> </p>
      </header>
    </div>
  );
}

export default App;
